import org.junit.After;import org.junit.Before;import org.junit.Test;import java.io.ByteArrayInputStream;import java.io.ByteArrayOutputStream;import java.io.InputStream;import java.io.PrintStream;import static org.junit.jupiter.api.Assertions.assertEquals;public class CoveringSegmentsTest {    private final InputStream systemIn = System.in;    private final PrintStream systemOut = System.out;    private ByteArrayInputStream testIn;    private ByteArrayOutputStream testOut;    @Before    public void setUpOutput() {        testOut = new ByteArrayOutputStream();        System.setOut(new PrintStream(testOut));    }    private void provideInput(String data) {        testIn = new ByteArrayInputStream(data.getBytes());        System.setIn(testIn);    }    private String getOutput() {        return testOut.toString();    }    @After    public void restoreSystemInputOutput() {        System.setIn(systemIn);        System.setOut(systemOut);    }    @Test    public void testCase1() {        final String input = "4\n" +                "4 7\n" +                "1 3\n" +                "2 5\n" +                "5 6";        provideInput(input);        CoveringSegments.main(new String[0]);        String expected = "2\n" +                "3 6 ";        assertEquals(expected, getOutput());    }    @Test    public void testCase2() {        final String input = "6\n" +                "14 16\n" +                "12 14\n" +                "12 14\n" +                "15 15\n" +                "17 18\n" +                "17 19";        provideInput(input);        CoveringSegments.main(new String[0]);        String expected = "3\n" +                "14 15 18 ";        assertEquals(expected, getOutput());    }    @Test    public void testCase3() {        final String input = "12\n" +                "17 18\n" +                "17 19\n" +                "22 23\n" +                "22 24\n" +                "23 23\n" +                "22 24\n" +                "25 27\n" +                "26 28\n" +                "26 26\n" +                "25 27\n" +                "26 28\n" +                "29 29";        provideInput(input);        CoveringSegments.main(new String[0]);        String expected = "4\n" +                "18 23 26 28 29 ";        assertEquals(expected, getOutput());    }}